<script>
  import svgToImage from 'svg-to-image'
  import SignaturePad from 'signature_pad'

  export default {
    name: 'teeth-canvas',

    props: {
      teethImage: {
        type: String,
        required: true
      }
    },

    data () {
      return {
        canvasMode: 'highlight',
        drawActive: false,
        highlightActive: true,
        highlightColor: '#72FBFD',
        canvasActive: false,
        canvasReady: false,
        signaturePad: null,
        canvasWidth: 0,
        canvasHeight: 0,
        teeth: [],
        swatches: [
          '#72FBFD',
          '#909090',
          '#EB3423',
          '#74FBB7',
          '#FDEB4E',
          '#54C2F8',
          '#75FA4F',
          '#A12CF6',
          '#000000',
          '#EA37C4'
        ]
      }
    },

    watch: {
      drawActive () {
        if (this.drawActive) {
          this.signaturePad.on()
        } else {
          this.signaturePad.off()
        }
      },
      canvasMode () {
        this.drawActive = this.canvasMode === 'draw'
        this.highlightActive = this.canvasMode === 'highlight'
      }
    },

    created () {
      this.initialTeeth()
    },

    mounted () {
      const element = document.getElementById('teeth-container')
      const style = window.getComputedStyle(element, null)

      setTimeout(() => {
        this.canvasWidth = parseFloat(style.getPropertyValue('width'))
        this.canvasHeight = parseFloat(style.getPropertyValue('height'))
      }, 100)
    },

    methods: {
      initialTeeth () {
        this.teeth = [
          { tooth_0: { highlighted: false, type: '', color: '' } },
          { tooth_1: { highlighted: false, type: '', color: '' } },
          { tooth_2: { highlighted: false, type: '', color: '' } },
          { tooth_3: { highlighted: false, type: '', color: '' } },
          { tooth_4: { highlighted: false, type: '', color: '' } },
          { tooth_5: { highlighted: false, type: '', color: '' } },
          { tooth_6: { highlighted: false, type: '', color: '' } },
          { tooth_7: { highlighted: false, type: '', color: '' } },
          { tooth_8: { highlighted: false, type: '', color: '' } },
          { tooth_9: { highlighted: false, type: '', color: '' } },
          { tooth_10: { highlighted: false, type: '', color: '' } },
          { tooth_11: { highlighted: false, type: '', color: '' } },
          { tooth_12: { highlighted: false, type: '', color: '' } },
          { tooth_13: { highlighted: false, type: '', color: '' } },
          { tooth_14: { highlighted: false, type: '', color: '' } },
          { tooth_15: { highlighted: false, type: '', color: '' } }
        ]
      },
      activateCanvas () {
        if (!this.canvasReady) {
          this.canvasReady = true
          const canvas = this.createHiDPICanvas(this.canvasWidth, this.canvasHeight, 2)
          this.signaturePad = new SignaturePad(canvas, {
            backgroundColor: 'rgba(255, 255, 255, 0)',
            penColor: 'rgb(0, 0, 0)'
          })
        }
        this.canvasActive = true
        this.canvasMode = 'highlight'
        this.drawActive = false
        this.highlightActive = true
        this.clearCanvas()
      },
      highlightedTeeth (toothIndex, color) {
        this.teeth[toothIndex][`tooth_${ toothIndex }`].highlighted = !this.teeth[toothIndex][`tooth_${ toothIndex }`].highlighted
        if (this.teeth[toothIndex][`tooth_${ toothIndex }`].highlighted) {
          this.teeth[toothIndex][`tooth_${ toothIndex }`].color = color
        } else {
          this.teeth[toothIndex][`tooth_${ toothIndex }`].color = '#ffffff'
        }
      },
      closeCanvas () {
        this.canvasActive = false
      },
      undoCanvas () {
        const data = this.signaturePad.toData()

        if (data) {
          data.pop()
          this.signaturePad.fromData(data)
        }
      },
      clearCanvas () {
        const canvas1 = this.signaturePad.canvas
        const canvas2 = document.getElementById('teeth-canvas-2')
        const ctx = canvas1.getContext('2d')
        const ctx2 = canvas2.getContext('2d')
        ctx.clearRect(0, 0, this.canvasWidth, this.canvasHeight)
        ctx2.clearRect(0, 0, this.canvasWidth, this.canvasHeight)
        this.signaturePad.clear()
        this.initialTeeth()
        this.$store.commit(`${ this.teethImage }-teeth/setImageData`, '')
      },
      saveCanvas () {
        const svg = document.getElementById('teeth-image').outerHTML
        const canvas1 = this.signaturePad.canvas
        const canvas2 = document.getElementById('teeth-canvas-2')
        const ctx = canvas1.getContext('2d')
        const ctx2 = canvas2.getContext('2d')

        svgToImage(svg, (error, image) => {
          if (error) { throw error }
          ctx2.drawImage(canvas1, 0, 0, this.canvasWidth, this.canvasHeight)
          ctx.clearRect(0, 0, this.canvasWidth, this.canvasHeight)
          ctx.drawImage(image, 0, 0, this.canvasWidth, this.canvasHeight)
          ctx.drawImage(canvas2, 0, 0, this.canvasWidth, this.canvasHeight)
          const imageData = canvas1.toDataURL('image/png')
          this.$store.commit(`${ this.teethImage }-teeth/setImageData`, imageData)
        })
        this.closeCanvas()
      },
      createHiDPICanvas (width, height, ratio) {
        if (!ratio) { ratio = 1 }
        const canvas = document.getElementById('teeth-canvas')
        canvas.width = width * ratio
        canvas.height = height * ratio
        canvas.style.width = width + 'px'
        canvas.style.height = height + 'px'
        canvas.getContext('2d').setTransform(ratio, 0, 0, ratio, 0, 0)
        return canvas
      }
    }
  }
</script>
